import React, { useState, useEffect } from 'react'; 
import api from './services/api';

import beach from "./assets/beach.png";

function App() {
    const [tarefa_input , setTarefa_input] = useState('');

    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        async function loadTasks() {
            const response = await api.get("http://localhost:4040/items/read");

            setTasks(response.data);
        }

        loadTasks();
    }, []);

    async function handleSubmit(e){
        e.preventDefault();

        const response = await api.post("http://localhost:4040/items/create", {
            content: tasks,
        })

        console.log(response.data);
        setTarefa_input("");
    }

    return ( 
        <div>
            <section id="sectionTop">
                <h1 className="center">uTask</h1>

                <form className="center" onSubmit={handleSubmit} >
                    <input 
                    type="text" 
                    required 
                    placeholder="Add uma nova tarefa"
                    value={tarefa_input}
                    onChange={e => setTarefa_input(e.target.value)} >
                    </input>
                    <button type="submit" ></button>
                </form>

            </section>

            <section id="sectionMiddle" >
                <hr></hr>

                <h2 className="center" >TODO</h2>

                <div id="div-meio" className="center" >
                    {tasks.map(task => (
                        <p style={"color: red;"}>{tasks.content}</p>
                    ))};
                </div>

                <div id="nada" className="center" >
                    <img src={beach} alt="Foto de uma praia" className="" ></img>
                    <p>Nada por aqui!</p>
                </div>
            </section>

            <section id="sectionBottom" >
                <hr></hr>

                <h2 className="center" >DONE</h2>

                <div id="div-rodape" >
                </div>

                <div id="nada" className="center" >
                    <img src={beach} alt="Foto de uma praia" className="" ></img>
                    <p>Nada por aqui!</p>
                </div>
            </section>
        </div>
    );
}

export default App;
